// root url page
router.get("/", (req,res) => {
    res.render('movies', {movieArray: movieArray})
})

// add a movie object
router.post('/create', (req,res) => {
    let title = req.body.title
    let description = req.body.description
    let genre = req.body.genre
    let posterUrl = req.body.posterUrl

    let movieObject = {
        title: title,
        description: description,
        genre: genre,
        posterUrl: posterUrl,
        movieId: uuidv4()
    }
    movieArray.push(movieObject)
    console.log(movieObject)

    res.redirect('/movies')
})

// delete movie object
router.post('/delete', (req,res) => {
    let deleteId = req.body.movieId

    movieArray = movieArray.filter( movie => {
        if (movie.movieId != deleteId) {
            return movie
        }
    })

    res.redirect('/movies')
})

router.post('/filter', (req,res) => {
    let searchedGenre = req.body.searchedGenre
    
    let filteredArray = movieArray.filter(movie => {
        return movie.genre.toLowerCase() == searchedGenre.toLowerCase()
    })

    res.render('movies', {movieArray: filteredArray})
})

router.get('/api', (req,res) => {
    res.json(movieArray)
})

module.exports = router